<?phprequire '../../includes/app.php';incluirTemplate('header');use App\Vendedor;$id = $_GET['id'];$id = filter_var($id, FILTER_VALIDATE_INT);//valida que sea un id validoif (!$id) {    header('/bienesraices/index.php');}//busca el vendedor $vendedor = Vendedor::find($id);$errores = $vendedor::getErrores();//asigna los valores de la bd a las variables$nombre = $vendedor->nombre;$apellido = $vendedor->apellido;$telefono = $vendedor->telefono;if ($_SERVER['REQUEST_METHOD'] == "POST") {    $args = $_POST["vendedor"];    $vendedor->sincronizar($args);    $errores = $vendedor->validar();    if (empty($errores)) {        $vendedor->guardar();    }}?><main class="contenedor seccion">		<h1>Actualizar Vendedor</h1>		<a href="../index.php" class="boton boton-verde">Volver al panel</a>    <?php foreach ($errores as $e) : ?>				<div class="alerta error">            <?php echo $e; ?>				</div>    <?php endforeach; ?>		<form method="POST" class="formulario" enctype="multipart/form-data">        <?php require '../../includes/templates/formulario_vendedores.php' ?>				<input type="submit" value="Actualizar Vendedor" class="boton boton-verde">		</form></main><?phpincluirTemplate('footer');?>