<?phprequire '../../includes/app.php';use App\Vendedor;incluirTemplate('header');$vendedor = new Vendedor();$errores = Vendedor::getErrores();if($_SERVER['REQUEST_METHOD'] == 'POST'){    //obtiene los datos del POST    $args = $_POST['vendedor'];        //pasa los datos del POST a la clase    $vendedor = new Vendedor($args);        $errores = $vendedor->validar();        //si no hay errores guarda en la db    if(empty($errores)){        $vendedor->crear();    }}?><main class="contenedor seccion">    <h1>Crear</h1>    <a href="../index.php" class="boton boton-verde">Volver al panel</a>    <?php foreach ($errores as $e) : ?>        <div class="alerta error">            <?php echo $e; ?>        </div>    <?php endforeach; ?>    <form method="POST" class="formulario" action="/bienesraices/admin/vendedores/crear.php"          enctype="multipart/form-data">        <?php require '../../includes/templates/formulario_vendedores.php'; ?>        <input type="submit" value="Crear Propiedad" class="boton boton-verde">    </form></main>